<script type="text/javascript">

    var EH_MOBILE_MEGA_MENU_LOGIN = EH_MOBILE_MEGA_MENU_LOGIN || {};
    EH_MOBILE_MEGA_MENU_LOGIN.MobileMegaMenuLogin = function () {

        var NameOfNameSpace = "EH_MOBILE_MEGA_MENU_LOGIN";
        var NameOfForm = "MobileMegaMenuLogin";

        var HasValidFndtnAbideEventBeenTriggered = false;

        var ErrorDivSelector = 'div.row-wrapper.emergency-text.module-space';

        function SwitchToHttpsUsingHttpsRequired() {
            try {
                var EnforceHttpsFromContent = ConvertToBoolean("True");

                var HttpsRequiredTrueComparisonValue = "";
                if (EnforceHttpsFromContent)
                    HttpsRequiredTrueComparisonValue = "Yes";

                var HttpsRequired = "";
                if (EnforceHttpsFromContent)
                    HttpsRequired = "Yes";

                if (HttpsRequired == HttpsRequiredTrueComparisonValue) {
                    var UseHttpsOnPersonalisationForms = ConvertToBoolean("True");
                    if (UseHttpsOnPersonalisationForms) {
                        if (window.location.href.toLowerCase().indexOf("https://") <= -1) {
                            //                            window.location.href = window.location.href.toLowerCase().replace("http://", "https://");   // JFD - uncommenting this causes http/https cycling
                        }
                    }
                }
            }
            catch (e) {
                Debug("Exception occurred in SwitchToHttpsUsingHttpsRequired() - " + e.message)
            }
        }

        function UnbindElementFromEvent(ElementAsObject, EventName) {
            try {
                ElementAsObject.unbind(EventName);
            }
            catch (e) {
                console.log("Exception message from UnbindElementFromEvent() within MobileMegaMenuLogin.ascx: " + e.message)
            }
        }

        $(document).ready(function () {

            SwitchToHttpsUsingHttpsRequired();

            var ProcessMobileMegaMenuLoginFormFunction = "EH_MOBILE_MEGA_MENU_LOGIN.MobileMegaMenuLogin.ProcessMobileMegaMenuLoginForm();"
            var IsInternetExplorer9 = (GetInternetExplorerVersion() == 9);

            // If IE9 then the presence of a placeholder attribute will cause a problem with data-abide client-side validation
            if (IsInternetExplorer9) {
                $('input#__LoginUserName').removeAttr("placeholder");
                $('input#__LoginPassword').removeAttr("placeholder");
                $('input#__LoginRememberMe').removeAttr("placeholder");
            }

            $('#MobileMegaMenuLoginForm').on('valid.fndtn.abide', function () {
                if (!HasValidFndtnAbideEventBeenTriggered)
                    (new Function(ProcessMobileMegaMenuLoginFormFunction))();

                HasValidFndtnAbideEventBeenTriggered = true;
            });
        });

        function Debug(msg, showAlert) {
            showAlert = typeof (showAlert) !== 'undefined' ? showAlert : false;
            try {
                if (window.console)
                    console.log(msg);

                if (showAlert === true)
                    alert(msg);
            }
            catch (e) {
                // if fails - give up
            }
        }

        function SpinningCursor(Spin, NextOrPrev) {
            try {

                NextOrPrev = typeof (NextOrPrev) !== 'undefined' ? NextOrPrev.toLowerCase() : "next";

                var BaseSelector = '.button.' + NextOrPrev + '.arrow';

                if (Spin === true) {
                    $(BaseSelector).text("Please Wait");
                    $(BaseSelector).append("<span></span>");
                    $(BaseSelector + ' span').css("background-image", "url(/static/Icons/ajax_loader.gif)");
                    $(BaseSelector + ' span').css("background-size", "30px");
                }
                else {

                    if (NextOrPrev === "next")
                        $(BaseSelector).text("NEXT");
                    else if (NextOrPrev === "previous")
                        $(BaseSelector).text("PREVIOUS");

                    $(BaseSelector + ' span').remove();
                    $(BaseSelector).append("<span></span>");
                }
            }
            catch (e) {
                Debug("An error has occurred in " + NameOfNameSpace + "." + NameOfForm + ".SpinningCursor() associated with the exception: " + e.message);
            }
        }

        function ProcessMobileMegaMenuLoginForm() {
            GoToPage("/UserAccount/Login", "Login");
        }

        function GoToPage(Url, Type, QueryString) {
            try {
                var CurrentPage = window.location.href;

                if (Url == "#" || Url == "")
                    Url = AddQueryStringToUrl(CurrentPage, QueryString);
                else
                    Url = AddQueryStringToUrl(Url, QueryString);

                window.location.href = Url;
            }
            catch (e) {
                Debug("An error has occurred in " + NameOfNameSpace + "." + NameOfForm + ".GoToPage() associated with the exception: " + e.message);
            }
            finally {
                SpinningCursor(false);
            }
        }

        function AddQueryStringToUrl(Url, QueryStringValue) {
            if (QueryStringValue != undefined && QueryStringValue !== "") {
                if (Url.indexOf(QueryStringValue) <= -1) {
                    if (Url.indexOf('?') > -1)
                        Url = Url + "&uid=" + QueryStringValue;
                    else {
                        if (!endsWith(Url, "/"))
                            Url = Url + "/";

                        Url = Url + "?uid=" + QueryStringValue;
                    }
                }
            }

            return Url;
        }

        function endsWith(str, suffix) {
            try {
                return str.indexOf(suffix, str.length - suffix.length) !== -1;
            }
            catch (e) {
                return false;
            }
        }

        function GetInternetExplorerVersion() {
            // Returns the version of Internet Explorer or a -1 (indicating the use of another browser).
            var rv = -1; // Return value assumes failure.
            try {
                if (navigator.appName === 'Microsoft Internet Explorer') {
                    var ua = navigator.userAgent;
                    var re = new RegExp("MSIE ([0-9]{1,}[\.0-9]{0,})");
                    if (re.exec(ua) != null) {
                        rv = parseFloat(RegExp.$1);
                    }
                }
            }
            catch (e) {
                Debug("An error has occurred in " + NameOfNameSpace + "." + NameOfForm + ".GetInternetExplorerVersion() associated with the exception: " + e.message);
            }
            return rv;
        }

        function ProcessMobileMegaMenuLogout() {
            GoToPage("/UserAccount/Logout", "Logout");
        }

        var PublicObjects = {
            "ProcessMobileMegaMenuLoginForm": function () { ProcessMobileMegaMenuLoginForm(); },
            "GoToPage": function (Url, Type) { GoToPage(Url, Type); },
            "ProcessMobileMegaMenuLogout": function () { ProcessMobileMegaMenuLogout(); }
        };

        return PublicObjects;
    }();

</script>

