                    default:                     
                        $(".off-canvas-wrap").removeClass("offcanvas-overlap");
                        if ($(".right-submenu").hasClass("offcanvas-overlap")) {
                            $(".right-submenu").removeClass("offcanvas-overlap");
                        }
                        //$('html, body').removeClass("scrollStop");                         
                }
            };

            // Main menu control
            $(".mobile-mega-menu-toggle").click(function () {              
                regularMegaMenuControl("toggle");          
                if ($('.off-canvas-wrap').hasClass('offcanvas-overlap')) {
                    $("body").addClass('overflow-hidden')
                }
                else {
                    $("body").removeClass('overflow-hidden')
                }
            });
            // Close mega menu on device rotation
            $(window).on('orientationchange', function () {                 
                regularMegaMenuControl("close");
            });
            // Close mega menu if window is resized above mobile breakpoint
            $(window).resize(function () {
                if (parseInt($(window).width()) > 1023) {
                    regularMegaMenuControl("close");
                }
            });
        };

        // ios7 mega menu performance fix. Removes all css transforms from off-canvas module and replaces them with JQuery-UI animation
        function ios7MegaMenu() {
            // Remove all CSS transforms from off canvas menu and hide the menu
            $(".right-off-canvas-menu, .right-off-canvas-menu *").css({
                "-webkit-transform": "none",
                "transform": "none"
            });
            $(".right-off-canvas-menu").hide();            

            function ios7MegaMenuControl(action) {
                switch (action) {
                    case "toggle":
                        $(".right-off-canvas-menu").toggle("slide", { direction: "right" }, 500);
                        $(".right-submenu").hide("slide", { direction: "right" }, 500);
                        $(".off-canvas-wrap").toggleClass("offcanvas-overlap", 550);                           
                        // $('html, body').toggleClass("scrollStop");
                        break;
                    case "close":
                        $(".right-off-canvas-menu").hide("slide", { direction: "right" }, 500);
                        $(".right-submenu").hide("slide", { direction: "right" }, 500);
                        $(".off-canvas-wrap").removeClass("offcanvas-overlap", 550);
                        //$('html, body').removeClass("scrollStop");
                        break;
                    case "open":
                        $(".right-off-canvas-menu").show("slide", { direction: "right" }, 500);                        
                       
                        //$('html, body').addClass("scrollStop");
                        break;
                    default:
                        $(".right-off-canvas-menu").hide("slide", { direction: "right" }, 500);
                        $(".right-submenu").hide("slide", { direction: "right" }, 500);
                        $(".off-canvas-wrap").removeClass("offcanvas-overlap", 550);                        
                        //$('html, body').removeClass("scrollStop");
                };
            };

            $(".mobile-mega-menu-toggle").click(function () {                
                ios7MegaMenuControl("toggle");
            });

            $(".exit-off-canvas").click(function () {                
                ios7MegaMenuControl("close");
            });

            $(window).on("orientationchange", function () {                  
                ios7MegaMenuControl("close");
            });

            $(window).resize(function () {
                if (parseInt($(window).width()) > 1023) {                  
                    ios7MegaMenuControl("close");
                }
            });

            // Main menu functions
            $("li.has-submenu > a").click(function () {
                $(this).siblings(".right-submenu").toggle("slide", { direction: "right" }, 500);                 
            });
            $("li.back > a").click(function () {
                $(this).closest(".right-submenu").toggle("slide", { direction: "right" }, 500);
            });
            // Close menu on link click
            //$(".off-canvas-wrap ul li a:not([href='#'])").click(function () {
            //    ios7MegaMenuControl("close");
            //});

            // Better solution which also fires when back/forward buttons are used
            //$(window).unload(function () {
            //    ios7MegaMenuControl("close");
            //});

            // Even better solution for ios7
            window.onpageshow = function () {
                ios7MegaMenuControl("close");
            };
            window.onpagehide = function () {
                ios7MegaMenuControl("close");
            };
        };

        // Re-enable scrolling when overlay clicked
        //$(".exit-off-canvas").click(function () {
        //    $('html, body').removeClass("scrollStop");
        //});

        // Check if browser is ios7 and run the corresponding function
        if (navigator.userAgent.match(/(iPad|iPhone|iPod touch);.*CPU.*OS 7_\d/i)) {
            ios7MegaMenu();
        }
        else {
            regularMegaMenu();
        }
    });

</script>


